function formatReadedPhoneNumber(phoneNumber) {
    // Convert the input to a string in case it's a number
    const phoneStr = phoneNumber.toString();

    // Validate that the phone number has exactly 10 digits
    if (!/^\d{10}$/.test(phoneStr)) {
        throw new Error('Phone number must be exactly 10 digits');
    }

    const areaCode = phoneStr.substring(0, 3);
    const centralOfficeCode = phoneStr.substring(3, 6);
    const lineNumber = phoneStr.substring(6, 10);

    // Combine with hyphens
    return `${areaCode}-${centralOfficeCode}-${lineNumber}`;
}

// Function to format the phone number input
function formatPhoneNumber() {
    var phoneNumberInside = document.getElementById(
            'phone-number');
    if (phoneNumberInside !== null) {

        // Add the event listener once the input is found
        phoneNumberInside.addEventListener('input', function(e) {
            let input = e.target.value.replace(
                /\D/g, ''
            ); // Remove non-numeric characters
            let formattedNumber = '';

            // Format the number based on its length
            if (input.length > 0) {
                formattedNumber = input.substring(0,
                    3); // First 3 digits
            }
            if (input.length >= 4) {
                formattedNumber += '-' + input
                    .substring(3,
                        6); // Next 3 digits
            }
            if (input.length >= 7) {
                formattedNumber += '-' + input
                    .substring(6,
                        10); // Last 4 digits
            }

            e.target.value =
                formattedNumber; // Update the input field with the formatted value
        });
    } else {
        setTimeout(formatPhoneNumber,
            1000); // Retry after 1 second if not found
    }
}

function splitPhoneNumber(phoneNumber) {
    if (typeof phoneNumber !== 'string') {
        throw new Error('Phone number must be a string');
    }

    // Ensure the phone number starts with '+'
    if (!phoneNumber.startsWith('+')) {
        throw new Error('Phone number must start with "+"');
    }

    // Remove the '+' for processing
    const numberWithoutPlus = phoneNumber.slice(1);

    // Country codes can be 1 to 3 digits
    for (let i = 3; i >= 1; i--) {
        const potentialCode = numberWithoutPlus.slice(0, i);
        if (countryCodes.includes(potentialCode)) {
            const countryCode = `+${potentialCode}`;
            const localNumber = numberWithoutPlus.slice(i);

            let parsedNumber;
            if (localNumber.toString().length == 10){
                parsedNumber = formatReadedPhoneNumber(localNumber)
            } else {
                parsedNumber = localNumber;
            }

            const countrySelect = document.getElementById('country-code');
            if (countrySelect) {
                countrySelect.value = countryCode;
            }

            // Optional: Validate local number length based on country code
            // This requires additional data and is beyond the current scope

            return {
                countryCode,
                phoneNumber: parsedNumber
            };
        }
    }

    throw new Error('Country code not found or invalid');
}


