function validateForm() {
    // Selecciona el formulario
    var form = document.querySelector('.inputsContainer');

    if (!form) {
        console.error("Formulario no encontrado.");
        return false;
    }

    // Selecciona todos los campos input, select y textarea dentro del formulario
    var inputs = form.querySelectorAll('input, select, textarea');


    var isValid = true; // Variable para rastrear la validez del formulario

    for (var i = 0; i < inputs.length; i++) {
        var input = inputs[i];
        var value = input.value.trim();
        var errorMessageElement = form.querySelector(`#${input.id}-error`);

        // Verifica si el campo es requerido
        if (input.hasAttribute('required')) {

            if (value === '') {
                input.classList.add('invalid');
                isValid = false;

                if (errorMessageElement) {
                    errorMessageElement.textContent = 'Este campo es obligatorio.';
                    errorMessageElement.classList.add('active');
                }
            } else {
                input.classList.remove('invalid');

                if (errorMessageElement) {
                    errorMessageElement.textContent = '';
                    errorMessageElement.classList.remove('active');
                }
            }
        }

        // Validaci贸n espec铆fica para el campo de n煤mero de tel茅fono
        if (input.id === 'phone-number') {
            // Elimina todos los caracteres que no sean d铆gitos
            var digits = value.replace(/\D/g, '');

            if (digits.length !== 10) {
                input.classList.add('invalid');
                isValid = false;

                if (errorMessageElement) {
                    errorMessageElement.textContent = 'El n煤mero de tel茅fono debe tener exactamente 10 d铆gitos.';
                    errorMessageElement.classList.add('active');
                }
            } else {
                input.classList.remove('invalid');

                if (errorMessageElement) {
                    errorMessageElement.textContent = '';
                    errorMessageElement.classList.remove('active');
                }
            }
        }

        // Validaci贸n espec铆fica para el campo de correo electr贸nico
        if (input.id === 'email') {
            // Expresi贸n regular para validar el formato del correo electr贸nico
            var emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

            if (!emailRegex.test(value)) {
                input.classList.add('invalid');
                isValid = false;


            } else {
                input.classList.remove('invalid');

                if (errorMessageElement) {
                    errorMessageElement.textContent = '';
                    errorMessageElement.classList.remove('active');
                }
            }
        }
    }

    if (!isValid) {
        // Opcional: Enfoca el primer campo inv谩lido
        var firstInvalid = form.querySelector('.invalid');
        if (firstInvalid) {
            firstInvalid.focus();
        }

        return false; // La validaci贸n falla
    }

    return true; // La validaci贸n es exitosa
}



