function errorCodeParser(parsedResponse) {
  try {
    const jsonResponse = parsedResponse.statusCode;
    const jsonBody = parsedResponse.body;

    let errorMessage;

    try {
      // Attempt to extract "message" using a regex pattern
      const pattern = /'message':\s*"([^"]*)"/;
      const match = pattern.exec(jsonBody);
      if (match && match[1]) {
        errorMessage = match[1];
      } else {
        throw new Error("Message pattern not found");
      }
    } catch (regexError) {
      // If regex fails, attempt to parse the body as JSON after replacing single quotes
      try {
        const sJson = jsonBody.replace(/'/g, '"'); // Replace all single quotes with double quotes
        const data = JSON.parse(sJson);
        errorMessage = data.message;
      } catch (jsonError) {
        console.error("Error parsing JSON body:", jsonError);
        errorMessage = null;
      }
    }

    if (errorMessage) {
      return [jsonResponse, errorMessage];
    } else {
      // If no error message found, attempt to extract "email"
      const bodyParsed = JSON.parse(jsonBody);
      const nameResponse = bodyParsed.email;
      return [jsonResponse, nameResponse];
    }
  } catch (error) {
    console.error("Error in errorCodeParser:", error);
    return [null, null];
  }
}


